Dashmix.onLoad(class{static initValidation(){Dashmix.helpers("jq-validation"),jQuery(".js-validation-signup")
.validate({rules:{
    "username":{required:!0,minlength:3},
    "name":{required:!0,minlength:3},
    "email":{required:!0,email:!0},
    "password":{required:!0,minlength:6},
    "password-confirm":{required:!0,equalTo:"#password"},
    "terms":{required:!0}},
    messages:{
    "username":{required:"Please enter a username",minlength:"Your username must consist of at least 3 characters"},
    "name":{required:"Please enter a name",minlength:"Your name must consist of at least 3 characters"},
    "email":"Please enter a valid email address",
    "password":{required:"Please provide a password",minlength:"Your password must be at least 6 characters long"},
    "password-confirm":{required:"Please provide a password",minlength:"Your password must be at least 6 characters long",equalTo:"Please enter the same password as above"},
    "terms":"You must agree to the service terms!"}})}static init(){this.initValidation()}}.init());

    $(function() {
        Array.prototype.filter.call($('.js-validation-signup'), function(form) {
            form.addEventListener('submit', function(event) {
                if (form.checkValidity() === false) {
                    event.preventDefault();
                    event.stopPropagation();
                }
                form.classList.add('is-valid');
            }, false);
        });
    });
